describe('MCP server lifecycle', () => {
  it('should create MCP server lazily', async () => {
    const db = await createTestDatabase();

    // First MCP request creates server
    await db.fetch(new Request('http://internal/mcp', { method: 'GET' }));

    // Subsequent requests reuse server
    const response = await db.fetch(
