      new Request('http://internal/insertOne', {
        method: 'POST',
        body: JSON.stringify({ collection: 'test', document: { name: 'Alice' } })
      })
    );

    // Search tool should find the document
    const response = await db.fetch(
      new Request('http://internal/mcp', {
