// src/agentfs/filesystem.ts

export class AgentFilesystem {
  private db: MondoDatabase;
  private collection = '__agentfs.files';

  constructor(db: MondoDatabase) {
    this.db = db;
  }

  async writeFile(path: string, content: string, metadata?: Record<string, unknown>): Promise<void> {
    const normalizedPath = this.normalizePath(path);
    const now = new Date();

    await this.db.updateOne(this.collection,
      { _id: normalizedPath },
      {
        $set: {
          path: normalizedPath,
          content,
          metadata: metadata || {},
          updatedAt: now
        },
        $setOnInsert: {
          createdAt: now
        }
      },
      { upsert: true }
    );
  }

  async readFile(path: string): Promise<string | null> {
    const doc = await this.db.findOne(this.collection, { 
      _id: this.normalizePath(path) 
    });
    return doc?.content ?? null;
  }

  async deleteFile(path: string): Promise<boolean> {
    const result = await this.db.deleteOne(this.collection, {
      _id: this.normalizePath(path)
    });
    return result.deletedCount > 0;
  }

  async existsFile(path: string): Promise<boolean> {
    const doc = await this.db.findOne(this.collection, {
      _id: this.normalizePath(path)
    }, { projection: { _id: 1 } });
    return doc !== null;
  }

  async readdir(path: string): Promise<string[]> {
    const prefix = this.normalizePath(path);
    const pattern = prefix === '/' 
      ? '^/[^/]+$' 
      : `^${prefix}/[^/]+$`;

    const docs = await this.db.find(this.collection, {
      path: { $regex: pattern }
    });

    return docs.map(d => d.path.split('/').pop()!);
  }

  private normalizePath(path: string): string {
    if (!path.startsWith('/')) path = '/' + path;
    return path.replace(/\/+/g, '/').replace(/\/$/, '') || '/';
  }
}
```

