// src/agentfs/kv.ts

export class AgentKV {
  private db: MondoDatabase;
  private collection = "__agentfs.kv";

  constructor(db: MondoDatabase) {
    this.db = db;
  }

  async set(key: string, value: unknown): Promise<void> {
    const now = new Date();
    await this.db.updateOne(this.collection,
      { _id: key },
      {
        $set: { key, value, updatedAt: now },
        $setOnInsert: { createdAt: now }
      },
      { upsert: true }
    );
  }

  async get<T = unknown>(key: string): Promise<T | null> {
    const doc = await this.db.findOne(this.collection, { _id: key });
    return doc?.value ?? null;
  }

  async delete(key: string): Promise<boolean> {
    const result = await this.db.deleteOne(this.collection, { _id: key });
    return result.deletedCount > 0;
  }

  async has(key: string): Promise<boolean> {
    const doc = await this.db.findOne(this.collection, 
      { _id: key }, 
      { projection: { _id: 1 } }
    );
    return doc \!== null;
  }

  async keys(prefix?: string): Promise<string[]> {
    const query = prefix 
      ? { key: { $regex: "^" + prefix } }
      : {};
    const docs = await this.db.find(this.collection, query);
    return docs.map(d => d.key);
  }
}
```

