// src/agentfs/toolcalls.ts

export class AgentToolCalls {
  private db: MondoDatabase;
  private collection = "__agentfs.toolcalls";

  constructor(db: MondoDatabase) {
    this.db = db;
  }

  async record(
    tool: string, 
    inputs: unknown, 
    outputs: unknown,
    options?: { startTime?: Date; endTime?: Date }
  ): Promise<string> {
    const now = new Date();
    const entry: AgentFSToolCall = {
      _id: new ObjectId().toHexString(),
      tool,
      inputs,
      outputs,
      timestamp: now,
      durationMs: options?.startTime && options?.endTime
        ? options.endTime.getTime() - options.startTime.getTime()
        : undefined
    };

    await this.db.insertOne(this.collection, entry);
    return entry._id;
  }

  async list(options?: { limit?: number; offset?: number }): Promise<AgentFSToolCall[]> {
    // Use aggregation for pagination
    return this.db.aggregate(this.collection, [
      { $sort: { timestamp: -1 } },
      { $skip: options?.offset ?? 0 },
      { $limit: options?.limit ?? 100 }
    ]);
  }

  async findByTool(tool: string): Promise<AgentFSToolCall[]> {
    return this.db.find(this.collection, { tool });
  }

  // INTENTIONALLY NOT IMPLEMENTED - enforces immutability
  async update(): Promise<never> {
    throw new Error("ToolCalls are immutable - append-only");
  }

  async delete(): Promise<never> {
    throw new Error("ToolCalls are immutable - cannot delete");
  }
}
