export interface VectorSearchResult {
  docIds: string[];
  scores: Map<string, number>;
}

export class VectorTranslator {
  constructor(
    private vectorize: VectorizeIndex,
    private ai?: Ai,
    private embeddingModel: string = '@cf/baai/bge-m3'
  ) {}

  async translateVectorSearch(
    stage: VectorSearchStage,
    collectionName: string
  ): Promise<VectorSearchResult> {
    let queryVector = stage.queryVector;

    if (!queryVector && stage.query) {
      if (!this.ai) {
        throw new Error('\$vectorSearch requires queryVector or query with AI binding');
      }
      const embedding = await this.ai.run(this.embeddingModel, {
        text: [stage.query]
      });
      queryVector = (embedding as any).data[0];
    }

    if (!queryVector) {
      throw new Error('\$vectorSearch requires queryVector or query');
    }

    const results = await this.vectorize.query(queryVector, {
      topK: Math.min(stage.limit, 100),
      namespace: collectionName,
      filter: stage.filter,
      returnMetadata: 'indexed'
    });

    const docIds = results.matches.map(m => m.id);
    const scores = new Map(results.matches.map(m => [m.id, m.score]));

    return { docIds, scores };
  }
}
