import { WorkerEntrypoint } from 'cloudflare:workers';

export interface DatabaseProxyProps {
  databaseId: string;
}

export class DatabaseProxy extends WorkerEntrypoint<
  Env,
  DatabaseProxyProps
> {
  private getDatabaseStub() {
    const { databaseId } = this.ctx.props;
    const id = this.env.MONDO_DATABASE.idFromName(databaseId);
    return this.env.MONDO_DATABASE.get(id);
  }

  private async callDatabase<T>(
    method: string,
    collection: string,
    ...args: unknown[]
  ): Promise<T> {
    if (!collection) {
      throw new Error('Collection name required');
    }

    const stub = this.getDatabaseStub();
    const response = await stub.fetch(
      new Request(`http://internal/${method}`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ collection, ...args[0] })
      })
    );

    if (!response.ok) {
      const error = await response.text();
      throw new Error(error);
    }

    return response.json();
  }

  // CRUD Methods
  async find(collection: string, filter: object = {}): Promise<Document[]> {
    return this.callDatabase('find', collection, { filter });
  }

  async findOne(collection: string, filter: object = {}): Promise<Document | null> {
    return this.callDatabase('findOne', collection, { filter });
  }

  async insertOne(collection: string, document: object): Promise<{ insertedId: string }> {
    if (!document) throw new Error('Document required');
    return this.callDatabase('insertOne', collection, { document });
  }

  async insertMany(collection: string, documents: object[]): Promise<{ insertedIds: string[] }> {
    return this.callDatabase('insertMany', collection, { documents });
  }

  async updateOne(
    collection: string,
    filter: object,
    update: object
  ): Promise<{ modifiedCount: number }> {
    return this.callDatabase('updateOne', collection, { filter, update });
  }

  async updateMany(
    collection: string,
    filter: object,
    update: object
  ): Promise<{ modifiedCount: number }> {
    return this.callDatabase('updateMany', collection, { filter, update });
  }

  async deleteOne(collection: string, filter: object): Promise<{ deletedCount: number }> {
    return this.callDatabase('deleteOne', collection, { filter });
  }

  async deleteMany(collection: string, filter: object): Promise<{ deletedCount: number }> {
    return this.callDatabase('deleteMany', collection, { filter });
  }

  async aggregate(collection: string, pipeline: object[]): Promise<Document[]> {
    return this.callDatabase('aggregate', collection, { pipeline });
  }

  async countDocuments(collection: string, filter: object = {}): Promise<number> {
    return this.callDatabase('countDocuments', collection, { filter });
  }

  // Collection Management
  async listCollections(): Promise<string[]> {
    const stub = this.getDatabaseStub();
    const response = await stub.fetch(
      new Request('http://internal/listCollections', { method: 'GET' })
    );
    return response.json();
  }

  async listDatabases(): Promise<string[]> {
    // MondoDB uses single database per DO, return current
    return [this.ctx.props.databaseId];
  }
}
