    return tool(
      "grep",
      "Search file contents with regex",
      {
        pattern: z.string().describe("Regex pattern"),
        glob: z.string().optional().describe("File filter"),
        caseInsensitive: z.boolean().optional()
      },
      async (args) => {
        const matches = await this.agent.grep(args.pattern, args);
        const text = matches
          .map(m => `${m.file}:${m.line}: ${m.content}`)
          .join("\n");
        return { content: [{ type: "text", text }] };
      }
    );
  }

  private createReadTool() {
    return tool(
      "read",
      "Read file contents",
      { path: z.string().describe("File path") },
      async ({ path }) => ({
        content: [{ 
          type: "text", 
          text: await this.agent.fs.readFile(path) ?? "" 
        }]
      })
    );
  }

  private createWriteTool() {
    return tool(
      "write",
      "Write file contents",
      { 
        path: z.string(), 
        content: z.string() 
      },
      async ({ path, content }) => {
        await this.agent.fs.writeFile(path, content);
        return { content: [{ type: "text", text: "OK" }] };
      }
    );
  }

  private createEditTool() {
    return tool(
      "edit",
      "Edit file by replacing text",
      {
        path: z.string(),
        old_string: z.string(),
        new_string: z.string()
      },
      async ({ path, old_string, new_string }) => {
        const content = await this.agent.fs.readFile(path);
        if (!content) throw new Error("File not found");
        const updated = content.replace(old_string, new_string);
        await this.agent.fs.writeFile(path, updated);
        return { content: [{ type: "text", text: "OK" }] };
      }
    );
  }
}

