// src/olap/router.ts
class QueryRouter {
  selectEngine(pipeline: AggregationStage[]): 'r2sql' | 'clickhouse' {
    if (this.hasJoins(pipeline)) return 'clickhouse';
    if (this.hasWindowFunctions(pipeline)) return 'clickhouse';
    if (this.hasComplexAggregation(pipeline)) return 'clickhouse';
    return 'r2sql';
  }

  async execute(spec: OlapStageSpec): Promise<Document[]> {
    const engine = spec.$olap.engine || this.selectEngine(spec.$olap.pipeline);

    switch (engine) {
      case 'r2sql':
        return this.r2sqlClient.execute(spec);
      case 'clickhouse':
        return this.clickhouseClient.execute(spec);
    }
  }
}
