# TDD GREEN Phase: Implement HTTP Transport

Implement minimal HTTP transport to pass RED tests.

## Implementation

### src/mcp/transport/http.ts
```typescript
import { StreamableHTTPServerTransport } from '@modelcontextprotocol/sdk/server/streamableHttp.js';
import type { McpServer } from '@modelcontextprotocol/sdk/server/mcp.js';

export interface CORSOptions {
  origin?: string;
  methods?: string[];
  headers?: string[];
  maxAge?: number;
}

export interface HttpMcpHandlerOptions {
  cors?: CORSOptions;
}

const DEFAULT_CORS: CORSOptions = {
  origin: '*',
  methods: ['GET', 'POST', 'DELETE', 'OPTIONS'],
  headers: ['Content-Type', 'mcp-session-id', 'mcp-protocol-version'],
  maxAge: 86400
};

export function createHttpMcpHandler(
  server: McpServer,
  options: HttpMcpHandlerOptions = {}
): (request: Request) => Promise<Response> {
  const cors = { ...DEFAULT_CORS, ...options.cors };

  // Session storage (in production, use Durable Object storage)
  const sessions = new Map<string, StreamableHTTPServerTransport>();

  return async (request: Request): Promise<Response> => {
    // Handle CORS preflight
    if (request.method === 'OPTIONS') {
      return new Response(null, {
        status: 204,
        headers: corsHeaders(cors)
      });
    }

    // Get or create session
    let sessionId = request.headers.get('mcp-session-id');
    if (!sessionId) {
      sessionId = crypto.randomUUID();
    }

    // Get or create transport for session
    let transport = sessions.get(sessionId);
    if (!transport) {
      transport = new StreamableHTTPServerTransport({
        sessionIdGenerator: () => sessionId!
      });
      await server.connect(transport);
      sessions.set(sessionId, transport);
    }

    try {
      // Handle based on method
      if (request.method === 'GET') {
        // Return SSE stream
        const response = await transport.handleRequest(request);
        return addCorsHeaders(response, cors, sessionId);
      } else if (request.method === 'POST') {
        // Handle JSON-RPC request
        const response = await transport.handleRequest(request);
        return addCorsHeaders(response, cors, sessionId);
      } else {
        return new Response('Method not allowed', {
          status: 405,
          headers: corsHeaders(cors)
        });
      }
    } catch (error) {
      return new Response(
        JSON.stringify({
          jsonrpc: '2.0',
          error: {
            code: -32603,
            message: error instanceof Error ? error.message : 'Internal error'
          }
        }),
        {
          status: 500,
          headers: {
            'Content-Type': 'application/json',
            ...Object.fromEntries(corsHeaders(cors).entries())
          }
        }
      );
    }
  };
}

function corsHeaders(cors: CORSOptions): Headers {
  const headers = new Headers();
  headers.set('Access-Control-Allow-Origin', cors.origin ?? '*');
  headers.set('Access-Control-Allow-Methods', (cors.methods ?? []).join(', '));
  headers.set('Access-Control-Allow-Headers', (cors.headers ?? []).join(', '));
  if (cors.maxAge) {
    headers.set('Access-Control-Max-Age', String(cors.maxAge));
  }
  return headers;
}

function addCorsHeaders(
  response: Response,
  cors: CORSOptions,
  sessionId: string
): Response {
  const headers = new Headers(response.headers);
  for (const [key, value] of corsHeaders(cors).entries()) {
    headers.set(key, value);
  }
  headers.set('mcp-session-id', sessionId);

  return new Response(response.body, {
    status: response.status,
    statusText: response.statusText,
    headers
  });
}
```

