// src/olap/r2sql/translator.ts
class R2SQLTranslator {
  translate(pipeline: AggregationStage[]): R2SQLQuery {
    const select = this.translateProject(pipeline);
    const where = this.translateMatch(pipeline);
    const groupBy = this.translateGroup(pipeline);
    const limit = this.extractLimit(pipeline);

    return {
      sql: `SELECT ${select} FROM ${this.table} WHERE ${where} GROUP BY ${groupBy} LIMIT ${limit}`,
      params: this.params
    };
  }

  private canTranslate(pipeline: AggregationStage[]): boolean {
    // Check for unsupported operations
    const unsupported = ['$lookup', '$unwind', '$graphLookup'];
    return !pipeline.some(s => unsupported.includes(Object.keys(s)[0]));
  }
}
