### src/mcp/sandbox/worker-evaluator.ts
```typescript
import type { WorkerLoader, Fetcher } from '@cloudflare/workers-types';
import { generateSandboxCode } from './template';
import type { DatabaseAccess } from '../types';

export interface EvaluatorResult {
  success: boolean;
  value?: unknown;
  logs: string[];
  error?: string;
}

export interface WorkerEvaluator {
  execute(): Promise<EvaluatorResult>;
}

export function createWorkerEvaluator(
  loader: WorkerLoader,
  dbAccess: DatabaseAccess,
  code: string,
  options: {
    timeout?: number;
    id?: string;
  } = {}
): WorkerEvaluator {
  const workerId = options.id ?? `sandbox-${Date.now()}-${Math.random().toString(36).slice(2)}`;

  return {
    async execute(): Promise<EvaluatorResult> {
      // Get or create the worker
      const worker = loader.get(workerId, () => ({
        compatibilityDate: '2025-06-01',
        compatibilityFlags: ['nodejs_compat'],
        mainModule: 'sandbox.js',
        modules: {
          'sandbox.js': generateSandboxCode(code)
        },
        env: {
          DB_PROXY: dbAccess.getProxy()
        },
        globalOutbound: null // Block all network access
      }));

      try {
        // Get the entrypoint and execute
        const entrypoint = worker.getEntrypoint();
        const result = await entrypoint.evaluate();

        return result as EvaluatorResult;
      } catch (error) {
        return {
          success: false,
          error: error instanceof Error ? error.message : 'Execution failed',
          logs: []
        };
      }
    }
  };
}
```

