import { transform } from 'esbuild';

export async function prepareUserCode(code: string): Promise<string> {
  // Check if code contains JSX or TypeScript
  if (containsJSX(code) || containsTypeScript(code)) {
    const result = await transform(code, {
      loader: 'tsx',
      jsx: 'transform',
      jsxFactory: 'h',
      target: 'es2022'
    });
    return result.code;
  }
  return code;
}

function containsJSX(code: string): boolean {
  return /<[A-Z][a-zA-Z]*[\s>]/.test(code) || /<\/[A-Z]/.test(code);
