```typescript
import type { DatabaseAccess, McpToolResponse, SearchResult } from '../types';

const MAX_RESULTS = 100;
const MAX_PREVIEW_LENGTH = 500;

export async function searchTool(
  dbAccess: DatabaseAccess,
  query: string
): Promise<McpToolResponse> {
  try {
    const { collection, filter, database } = parseQuery(query);

    // Execute search across collection(s)
    const documents = await executeSearch(dbAccess, collection, filter);

    // Format results to OpenAI Deep Research standard
    const results: SearchResult[] = documents
      .slice(0, MAX_RESULTS)
      .map(doc => formatSearchResult(doc, collection, database));

    return {
      content: [{
        type: 'text',
        text: JSON.stringify({ results })
      }]
    };
  } catch (error) {
    return {
      content: [{
        type: 'text',
        text: JSON.stringify({
          error: error instanceof Error ? error.message : 'Search failed'
        })
      }],
      isError: true
    };
  }
}

function parseQuery(query: string): {
  collection?: string;
  filter: object;
  database: string;
} {
  // Try to parse as JSON filter first
  try {
    const filter = JSON.parse(query);
    return { filter, database: 'default' };
  } catch {}

  // Try collection:query format
  const colonMatch = query.match(/^(\w+):\s*(.+)$/);
  if (colonMatch) {
    return {
      collection: colonMatch[1],
      filter: parseSimpleQuery(colonMatch[2]),
      database: 'default'
    };
  }

  // Treat as natural language - search all text fields
  return {
    filter: { $text: { $search: query } },
    database: 'default'
  };
}

function parseSimpleQuery(query: string): object {
  // Handle "field = value" syntax
  const match = query.match(/(\w+)\s*=\s*(.+)/);
  if (match) {
    return { [match[1]]: match[2].trim() };
  }
  return { $text: { $search: query } };
}

async function executeSearch(
  dbAccess: DatabaseAccess,
  collection: string | undefined,
  filter: object
): Promise<Document[]> {
  if (collection) {
    return dbAccess.find(collection, filter);
  }

  // Search all collections if none specified
  const collections = await dbAccess.listCollections();
  const allResults: Document[] = [];

  for (const coll of collections.slice(0, 10)) {
    const docs = await dbAccess.find(coll, filter);
    allResults.push(...docs.map(d => ({ ...d, _collection: coll })));
  }

  return allResults;
}

function formatSearchResult(
  doc: Document,
  collection: string,
  database: string
): SearchResult {
  const id = doc._id?.toString() ?? 'unknown';
  const coll = doc._collection ?? collection ?? 'unknown';

  return {
    id: `${database}.${coll}.${id}`,
    title: doc.title ?? doc.name ?? doc._id?.toString() ?? 'Untitled',
    url: `mongodb://${database}/${coll}/${id}`,
    text: createPreview(doc)
  };
}

function createPreview(doc: Document): string {
  const preview = JSON.stringify(doc, null, 2);
  if (preview.length > MAX_PREVIEW_LENGTH) {
    return preview.slice(0, MAX_PREVIEW_LENGTH) + '...';
  }
  return preview;
}
```

