```typescript
class PipelineClient {
  async sendWithRetry(events: CDCEvent[], maxRetries = 3): Promise<void> {
    for (let i = 0; i < maxRetries; i++) {
      try {
        await this.binding.send(events);
        return;
      } catch (e) {
        if (e.status === 429) {
          await this.sleep(Math.pow(2, i) * 1000);
        } else {
          throw e;
        }
      }
    }
  }
}
