import { Command } from 'commander';
import { createMcpServer } from 'mondodb/mcp';
import { createStdioTransport } from 'mondodb/mcp/transport/stdio';
import type { DatabaseAccess } from 'mondodb/mcp';

export interface McpServerOptions {
  connection?: string;
  local?: string;
  stdin?: NodeJS.ReadStream;
  stdout?: NodeJS.WriteStream;
  stderr?: NodeJS.WriteStream;
}

export async function startMcpServer(options: McpServerOptions): Promise<void> {
  const stdin = options.stdin ?? process.stdin;
  const stdout = options.stdout ?? process.stdout;
  const stderr = options.stderr ?? process.stderr;

  // Create database access based on mode
  const dbAccess = options.local
    ? await createLocalDatabaseAccess(options.local)
    : await createRemoteDatabaseAccess(options.connection!);

  // Create MCP server (no loader for CLI - use Miniflare fallback)
  const server = createMcpServer(dbAccess, undefined);

  // Create stdio transport
  const transport = createStdioTransport({ stdin, stdout });

  // Connect and start
  await server.connect(transport);

  stderr.write('MondoDB MCP server running on stdio\n');
  stderr.write('Tools: search, fetch, do\n');
}

export async function createRemoteDatabaseAccess(
  connectionString: string
): Promise<DatabaseAccess> {
  const baseUrl = connectionString.replace(/\/$/, '');

  async function call<T>(endpoint: string, body: object): Promise<T> {
    const response = await fetch(`${baseUrl}/${endpoint}`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(body)
    });

    if (!response.ok) {
      throw new Error(`Database error: ${await response.text()}`);
    }

    return response.json();
  }

  return {
    find: (collection, filter) => call('find', { collection, filter }),
    findOne: (collection, filter) => call('findOne', { collection, filter }),
    insertOne: (collection, document) => call('insertOne', { collection, document }),
    insertMany: (collection, documents) => call('insertMany', { collection, documents }),
    updateOne: (collection, filter, update) => call('updateOne', { collection, filter, update }),
    updateMany: (collection, filter, update) => call('updateMany', { collection, filter, update }),
    deleteOne: (collection, filter) => call('deleteOne', { collection, filter }),
    deleteMany: (collection, filter) => call('deleteMany', { collection, filter }),
    aggregate: (collection, pipeline) => call('aggregate', { collection, pipeline }),
    countDocuments: (collection, filter) => call('countDocuments', { collection, filter }),
    listCollections: () => call('listCollections', {}),
    listDatabases: () => Promise.resolve(['default']),
    getProxy: () => { throw new Error('getProxy not available in CLI mode'); }
  };
}

export async function createLocalDatabaseAccess(
  dbPath: string
): Promise<DatabaseAccess> {
  // Use better-sqlite3 or sql.js for local SQLite
  const { Database } = await import('better-sqlite3');
  const db = new Database(dbPath);

  // Initialize schema
  db.exec(`
    CREATE TABLE IF NOT EXISTS collections (id INTEGER PRIMARY KEY, name TEXT UNIQUE);
    CREATE TABLE IF NOT EXISTS documents (
      id INTEGER PRIMARY KEY,
      collection_id INTEGER,
      _id TEXT,
      data TEXT,
      FOREIGN KEY (collection_id) REFERENCES collections(id)
    );
    CREATE INDEX IF NOT EXISTS idx_doc_id ON documents(_id);
    CREATE INDEX IF NOT EXISTS idx_doc_coll ON documents(collection_id);
  `);

  // Implementation similar to MondoDatabase but synchronous
  return {
    find: async (collection, filter) => { /* ... */ },
    findOne: async (collection, filter) => { /* ... */ },
    // ... other methods
  };
}

export function registerMcpCommand(program: Command): void {
  program
    .command('mcp')
    .description('Start MCP server over stdio')
    .option('-c, --connection <url>', 'Remote MondoDB connection URL')
    .option('-l, --local <path>', 'Local SQLite database path')
    .action(async (options) => {
      if (!options.connection && !options.local) {
        console.error('Error: Either --connection or --local is required');
        process.exit(1);
      }

      await startMcpServer(options);
    });
}
```

