import { StdioServerTransport } from '@modelcontextprotocol/sdk/server/stdio.js';
import type { Readable, Writable } from 'node:stream';

export interface StdioTransportOptions {
  stdin?: Readable;
  stdout?: Writable;
  stderr?: Writable;
}

export function createStdioTransport(
  options: StdioTransportOptions = {}
): StdioServerTransport {
  const transport = new StdioServerTransport();

  // Override streams if provided (for testing)
  if (options.stdin || options.stdout) {
    // The SDK transport uses process.stdin/stdout by default
    // For custom streams, we need to wrap them
    return new CustomStdioTransport(options);
  }

  return transport;
}

/**
 * Custom stdio transport for testing with mock streams
 */
class CustomStdioTransport {
  private stdin: Readable;
  private stdout: Writable;
  private running = false;
  private messageHandler?: (message: unknown) => void;

  constructor(options: StdioTransportOptions) {
    this.stdin = options.stdin ?? process.stdin;
    this.stdout = options.stdout ?? process.stdout;
  }

  set onMessage(handler: (message: unknown) => void) {
    this.messageHandler = handler;
  }

  async start(): Promise<void> {
    this.running = true;
    let buffer = '';

    this.stdin.on('data', (chunk: Buffer | string) => {
      buffer += chunk.toString();

      // Process complete lines
      const lines = buffer.split('\n');
      buffer = lines.pop() ?? ''; // Keep incomplete line in buffer

      for (const line of lines) {
        if (line.trim()) {
          this.handleLine(line);
        }
      }
    });

    this.stdin.on('end', () => {
      this.running = false;
    });

    this.stdin.on('error', (error) => {
      this.sendError(-32603, error.message);
    });
  }

  private handleLine(line: string): void {
    try {
      const message = JSON.parse(line);
      if (this.messageHandler) {
        this.messageHandler(message);
      }
    } catch (error) {
      this.sendError(-32700, 'Parse error: Invalid JSON');
    }
  }

  async send(message: unknown): Promise<void> {
    const json = JSON.stringify(message);
    this.stdout.write(json + '\n');
  }

  private sendError(code: number, message: string): void {
    this.send({
      jsonrpc: '2.0',
      error: { code, message }
    });
  }

  isRunning(): boolean {
    return this.running;
  }

  async close(): Promise<void> {
    this.running = false;
  }
}

export { CustomStdioTransport };
```

