### src/mcp/sandbox/miniflare-evaluator.ts
```typescript
import { Miniflare } from 'miniflare';
import { generateSandboxCode } from './template';
import type { DatabaseAccess, EvaluatorResult } from '../types';

export interface MiniflareEvaluator {
  execute(): Promise<EvaluatorResult>;
  dispose(): void;
}

export function createMiniflareEvaluator(
  dbAccess: DatabaseAccess,
  code: string,
  options: { timeout?: number } = {}
): MiniflareEvaluator {
  let mf: Miniflare | null = null;

  return {
    async execute(): Promise<EvaluatorResult> {
      // Create Miniflare instance with sandbox code
      mf = new Miniflare({
        modules: true,
        script: generateSandboxCode(code),
        compatibilityDate: '2025-06-01',
        compatibilityFlags: ['nodejs_compat'],

        // Service bindings for DB access
        serviceBindings: {
          DB_PROXY: createDbProxyService(dbAccess)
        },

        // Block outbound network
        outboundService: () => {
          throw new Error('Network access is blocked in sandbox');
        }
      });

      try {
        const worker = await mf.getWorker();
        const result = await worker.evaluate();
        return result as EvaluatorResult;
      } catch (error) {
        return {
          success: false,
          error: error instanceof Error ? error.message : 'Execution failed',
          logs: []
        };
      }
    },

    dispose(): void {
      if (mf) {
        mf.dispose();
        mf = null;
      }
    }
  };
}

/**
 * Create a service that handles DB_PROXY RPC calls
 */
function createDbProxyService(dbAccess: DatabaseAccess) {
  return async (request: Request): Promise<Response> => {
    const url = new URL(request.url);
    const method = url.pathname.slice(1); // Remove leading /
    const body = await request.json();

    try {
      let result: unknown;

      switch (method) {
        case 'find':
          result = await dbAccess.find(body.collection, body.filter);
          break;
        case 'findOne':
          result = await dbAccess.findOne(body.collection, body.filter);
          break;
        case 'insertOne':
          result = await dbAccess.insertOne(body.collection, body.document);
          break;
        case 'insertMany':
          result = await dbAccess.insertMany(body.collection, body.documents);
          break;
        case 'updateOne':
          result = await dbAccess.updateOne(body.collection, body.filter, body.update);
          break;
        case 'updateMany':
          result = await dbAccess.updateMany(body.collection, body.filter, body.update);
          break;
        case 'deleteOne':
          result = await dbAccess.deleteOne(body.collection, body.filter);
          break;
        case 'deleteMany':
          result = await dbAccess.deleteMany(body.collection, body.filter);
          break;
        case 'aggregate':
          result = await dbAccess.aggregate(body.collection, body.pipeline);
          break;
        case 'countDocuments':
          result = await dbAccess.countDocuments(body.collection, body.filter);
          break;
        case 'listCollections':
          result = await dbAccess.listCollections();
          break;
        default:
          throw new Error(`Unknown method: ${method}`);
      }

      return Response.json(result);
    } catch (error) {
      return Response.json(
        { error: error instanceof Error ? error.message : 'Unknown error' },
        { status: 500 }
      );
    }
  };
}
