interface SearchResult {
  docId: string;
  score: number;
}

interface FusedResult {
  docId: string;
  fusedScore: number;
}

interface FusionWeights {
  vector?: number;
  text?: number;
}

export class HybridSearchTranslator {
  private readonly RRF_K = 60;  // Standard RRF constant

  rankFusion(
    vectorResults: SearchResult[],
    textResults: SearchResult[]
  ): FusedResult[] {
    const scores = new Map<string, number>();

    // Add vector search contributions
    vectorResults.forEach((r, rank) => {
      const rrfScore = 1 / (this.RRF_K + rank + 1);
      scores.set(r.docId, (scores.get(r.docId) || 0) + rrfScore);
    });

    // Add text search contributions
    textResults.forEach((r, rank) => {
      const rrfScore = 1 / (this.RRF_K + rank + 1);
      scores.set(r.docId, (scores.get(r.docId) || 0) + rrfScore);
    });

    return Array.from(scores.entries())
      .map(([docId, fusedScore]) => ({ docId, fusedScore }))
      .sort((a, b) => b.fusedScore - a.fusedScore);
  }

  scoreFusion(
    vectorResults: SearchResult[],
    textResults: SearchResult[],
    weights: FusionWeights = { vector: 0.5, text: 0.5 }
  ): FusedResult[] {
    const vectorWeight = weights.vector ?? 0.5;
    const textWeight = weights.text ?? 0.5;

    // Normalize vector scores (already 0-1 for cosine)
    const normalizedVector = this.normalizeScores(vectorResults);

    // Normalize text scores (FTS5 rank is negative, closer to 0 is better)
    const normalizedText = this.normalizeScores(textResults, true);

    // Combine scores
    const scores = new Map<string, number>();

    for (const r of normalizedVector) {
      scores.set(r.docId, (scores.get(r.docId) || 0) + r.score * vectorWeight);
    }

    for (const r of normalizedText) {
      scores.set(r.docId, (scores.get(r.docId) || 0) + r.score * textWeight);
    }

    return Array.from(scores.entries())
      .map(([docId, fusedScore]) => ({ docId, fusedScore }))
      .sort((a, b) => b.fusedScore - a.fusedScore);
  }

  private normalizeScores(
    results: SearchResult[], 
    invertNegative = false
  ): SearchResult[] {
    if (results.length === 0) return [];

    let scores = results.map(r => r.score);

    // Handle FTS5 negative scores
    if (invertNegative && scores.some(s => s < 0)) {
      const maxAbs = Math.max(...scores.map(Math.abs));
      scores = scores.map(s => 1 - (Math.abs(s) / maxAbs));
    }

    // Min-max normalization
    const min = Math.min(...scores);
    const max = Math.max(...scores);
    const range = max - min || 1;

    return results.map((r, i) => ({
      docId: r.docId,
      score: (scores[i] - min) / range
    }));
  }
}
