interface PipelineContext {
  vectorResults?: Array<{ docId: string; score: number }>;
  textResults?: Array<{ docId: string; score: number }>;
}

case '\$rankFusion':
  return this.translateRankFusion(context);

private translateRankFusion(context: PipelineContext): StageResult {
  const hybrid = new HybridSearchTranslator();
  const fused = hybrid.rankFusion(
    context.vectorResults || [],
    context.textResults || []
  );

  // Generate SQL to fetch in fused order
  const docIds = fused.map(f => f.docId);
  const scoreCase = this.generateScoreCase(fused);

  return {
    selectClause: \`*, \${scoreCase} as __rankFusionScore\`,
    whereClause: \`doc_id IN (\${docIds.map(() => '?').join(',')})\`,
    orderByClause: this.generateOrderCase(docIds),
    params: docIds
  };
}

private generateScoreCase(fused: FusedResult[]): string {
  return \`CASE doc_id \${fused.map(f => 
    \`WHEN '\${f.docId}' THEN \${f.fusedScore}\`
  ).join(' ')} END\`;
}
