### Implementation: `src/translator/search-translator.ts`

```typescript
interface TextClause {
  query: string;
  path?: string | string[];
  fuzzy?: { maxEdits?: number };
}

interface PhraseClause {
  query: string;
  path?: string | string[];
  slop?: number;
}

interface WildcardClause {
  query: string;
  path: string;
}

interface CompoundClause {
  must?: SearchClause[];
  should?: SearchClause[];
  mustNot?: SearchClause[];
  filter?: SearchClause[];
}

type SearchClause = 
  | { text: TextClause }
  | { phrase: PhraseClause }
  | { wildcard: WildcardClause }
  | { compound: CompoundClause };

interface SearchStage {
  index?: string;
  text?: TextClause;
  phrase?: PhraseClause;
  wildcard?: WildcardClause;
  compound?: CompoundClause;
}

interface SearchResult {
  ftsMatch: string;
  orderBy?: string;
  params: unknown[];
}

export class SearchTranslator {
  translateSearch(stage: SearchStage, collectionName: string): SearchResult {
    if (stage.compound) {
      return this.translateCompound(stage.compound);
    }
    if (stage.text) {
      return { ftsMatch: this.translateText(stage.text), params: [] };
    }
    if (stage.phrase) {
      return { ftsMatch: this.translatePhrase(stage.phrase), params: [] };
    }
    if (stage.wildcard) {
      return { ftsMatch: this.translateWildcard(stage.wildcard), params: [] };
    }
    throw new Error('Invalid \$search stage');
  }

  private translateText(clause: TextClause): string {
    const terms = clause.query.split(/\\s+/);
    const prefix = clause.path ? \`\${clause.path}:\` : '';
    return terms.map(t => \`\${prefix}\${this.escape(t)}\`).join(' ');
  }

  private translatePhrase(clause: PhraseClause): string {
    const prefix = clause.path ? \`\${clause.path}:\` : '';
    return \`\${prefix}"\${this.escape(clause.query)}"\`;
  }

  private translateWildcard(clause: WildcardClause): string {
    return \`\${clause.path}:\${clause.query}\`;
  }

  private translateCompound(compound: CompoundClause): SearchResult {
    const parts: string[] = [];

    if (compound.must?.length) {
      const mustParts = compound.must.map(c => this.clauseToFTS5(c));
      parts.push(\`(\${mustParts.join(' AND ')})\`);
    }

    if (compound.should?.length) {
      const shouldParts = compound.should.map(c => this.clauseToFTS5(c));
      parts.push(\`(\${shouldParts.join(' OR ')})\`);
    }

    if (compound.mustNot?.length) {
      const notParts = compound.mustNot.map(c => \`NOT \${this.clauseToFTS5(c)}\`);
      parts.push(notParts.join(' AND '));
    }

    return {
      ftsMatch: parts.join(' AND '),
      params: []
    };
  }

  private clauseToFTS5(clause: SearchClause): string {
    if ('text' in clause) return this.translateText(clause.text);
    if ('phrase' in clause) return this.translatePhrase(clause.phrase);
    if ('wildcard' in clause) return this.translateWildcard(clause.wildcard);
    if ('compound' in clause) return \`(\${this.translateCompound(clause.compound).ftsMatch})\`;
    throw new Error('Unknown clause type');
  }

  private escape(text: string): string {
    return text.replace(/["\\\\]/g, '\\\\$&');
  }
}
