  const ftsTable = \`\${collectionName}_fts\`;

  return {
    selectClause: \`d.*, fts.rank as __searchScore\`,
    fromClause: \`\${ftsTable} fts JOIN documents d ON d.id = fts.rowid\`,
    whereClause: \`\${ftsTable} MATCH ?\`,
    orderByClause: 'fts.rank',
    params: [searchResult.ftsMatch],
    transformsShape: false
  };
}
