private translateSearchStageWithHighlights(
  stage: SearchStage,
  collectionName: string
): StageResult {
  const ftsTable = \`\${collectionName}_fts\`;
  const highlightPath = stage.highlight?.path;

  let selectClause = 'd.*, fts.rank as __searchScore';

  if (highlightPath) {
    const colIndex = this.getColumnIndex(highlightPath, collectionName);
    selectClause += \`, highlight(\${ftsTable}, \${colIndex}, '<mark>', '</mark>') as __highlight_\${highlightPath}\`;
    selectClause += \`, snippet(\${ftsTable}, \${colIndex}, '<mark>', '</mark>', '...', 32) as __snippet_\${highlightPath}\`;
  }

  return {
    selectClause,
    fromClause: \`\${ftsTable} fts JOIN documents d ON d.id = fts.rowid\`,
    whereClause: \`\${ftsTable} MATCH ?\`,
    orderByClause: 'fts.rank',
    params: [/* ... */]
  };
}
