### Implementation: \`src/embedding/document-serializer.ts\`

\`\`\`typescript
import * as yaml from 'yaml';

export interface EmbeddingConfig {
  serializer?: 'yaml' | 'json' | 'text';
  fields?: string[];
  auto?: {
    include?: string[];
    exclude?: string[];
    maxDepth?: number;
  };
  maxTokens?: number;
}

const DEFAULT_EXCLUDE = [
  '_id',
  /.*Id$/,
  /.*_id$/,
  'createdAt', 'updatedAt', 
  'created_at', 'updated_at',
  'embedding', 'vector', '__v'
];

export function serializeForEmbedding(
  doc: Record<string, unknown>,
  config: EmbeddingConfig
): string {
  const filtered = filterDocument(doc, config);

  switch (config.serializer || 'yaml') {
    case 'yaml':
      return yaml.stringify(filtered);
    case 'json':
      return JSON.stringify(filtered, null, 2);
    case 'text':
      return extractText(filtered);
    default:
      return yaml.stringify(filtered);
  }
}

function filterDocument(
  doc: Record<string, unknown>,
  config: EmbeddingConfig,
  depth = 0
): Record<string, unknown> {
  const maxDepth = config.auto?.maxDepth ?? 3;
  const excludePatterns = [...DEFAULT_EXCLUDE, ...(config.auto?.exclude || [])];

  const result: Record<string, unknown> = {};

  for (const [key, value] of Object.entries(doc)) {
    if (shouldExclude(key, excludePatterns)) continue;

    if (typeof value === 'object' && value !== null && !Array.isArray(value)) {
      if (depth < maxDepth) {
        result[key] = filterDocument(value as Record<string, unknown>, config, depth + 1);
      }
    } else {
      result[key] = value;
    }
  }

  return result;
}

function shouldExclude(key: string, patterns: (string | RegExp)[]): boolean {
  return patterns.some(pattern => {
    if (typeof pattern === 'string') return key === pattern;
    return pattern.test(key);
  });
}

function extractText(obj: unknown, parts: string[] = []): string {
  if (typeof obj === 'string') {
    parts.push(obj);
  } else if (Array.isArray(obj)) {
    obj.forEach(item => extractText(item, parts));
  } else if (typeof obj === 'object' && obj !== null) {
    Object.values(obj).forEach(value => extractText(value, parts));
  }
  return parts.join(' ');
}
