### Implementation: \`src/embedding/embedding-manager.ts\`

\`\`\`typescript
import { serializeForEmbedding, EmbeddingConfig } from './document-serializer';

export class EmbeddingManager {
  constructor(
    private vectorize: VectorizeIndex,
    private ai: Ai,
    private config: EmbeddingConfig,
    private model: string = '@cf/baai/bge-m3'
  ) {}

  async embedDocument(
    collectionName: string,
    docId: string,
    document: Record<string, unknown>
  ): Promise<void> {
    const text = serializeForEmbedding(document, this.config);
    const response = await this.ai.run(this.model, { text: [text] });
    const metadata = this.extractMetadata(document);

    await this.vectorize.upsert([{
      id: docId,
      namespace: collectionName,
      values: (response as any).data[0],
      metadata
    }]);
  }

  async embedDocuments(
    collectionName: string,
    documents: Array<{ docId: string; document: Record<string, unknown> }>
  ): Promise<void> {
    const BATCH_SIZE = 100;

    for (let i = 0; i < documents.length; i += BATCH_SIZE) {
      const batch = documents.slice(i, i + BATCH_SIZE);
      const texts = batch.map(d => serializeForEmbedding(d.document, this.config));

      const response = await this.ai.run(this.model, { text: texts });

      const vectors = batch.map((d, idx) => ({
        id: d.docId,
        namespace: collectionName,
        values: (response as any).data[idx],
        metadata: this.extractMetadata(d.document)
      }));

      await this.vectorize.upsert(vectors);
    }
  }

  async deleteDocument(collectionName: string, docId: string): Promise<void> {
    await this.vectorize.deleteByIds([docId]);
  }

  private extractMetadata(doc: Record<string, unknown>): Record<string, any> {
    const metadata: Record<string, any> = {};
    for (const [key, value] of Object.entries(doc)) {
      if (this.isFilterableValue(value)) {
        metadata[key] = value;
      }
    }
    return metadata;
  }

  private isFilterableValue(value: unknown): boolean {
    return typeof value === 'string' ||
           typeof value === 'number' ||
           typeof value === 'boolean' ||
           value === null;
  }
}
