\`\`\`typescript
async insertOne(doc: Document): Promise<InsertOneResult> {
  const result = await this.doInsertOne(doc);

  if (this.shouldEmbed()) {
    this.ctx?.waitUntil(
      this.embeddingManager!.embedDocument(
        this.name,
        result.insertedId.toString(),
        doc
      )
    );
  }

  return result;
}

async updateOne(filter: Document, update: Document): Promise<UpdateResult> {
  const result = await this.doUpdateOne(filter, update);

  if (this.shouldEmbed() && result.modifiedCount > 0) {
    const updated = await this.findOne(filter);
    if (updated) {
      this.ctx?.waitUntil(
        this.embeddingManager!.embedDocument(
          this.name,
          updated._id.toString(),
          updated
        )
      );
    }
  }

  return result;
}

async deleteOne(filter: Document): Promise<DeleteResult> {
  const doc = await this.findOne(filter, { projection: { _id: 1 } });
  const result = await this.doDeleteOne(filter);

  if (this.shouldEmbed() && doc && result.deletedCount > 0) {
    this.ctx?.waitUntil(
      this.embeddingManager!.deleteDocument(this.name, doc._id.toString())
    );
  }

  return result;
}

private shouldEmbed(): boolean {
  return !!this.embeddingManager && 
         this.collectionConfig?.embedding?.enabled === true;
}
