import type { WorkerLoader } from '@cloudflare/workers-types';
import { createWorkerEvaluator } from '../sandbox/worker-evaluator';
import { createMiniflareEvaluator } from '../sandbox/miniflare-evaluator';
import type { DatabaseAccess, McpToolResponse, DoResult } from '../types';

export async function doTool(
  dbAccess: DatabaseAccess,
  loader: WorkerLoader | undefined,
  code: string,
  description?: string
): Promise<McpToolResponse> {
  const startTime = Date.now();

  // Choose evaluator based on environment
  const evaluator = loader
    ? createWorkerEvaluator(loader, dbAccess, code)
    : createMiniflareEvaluator(dbAccess, code);

  try {
    const executionResult = await evaluator.execute();
    const duration = Date.now() - startTime;

    const result: DoResult = {
      success: executionResult.success,
      result: executionResult.value,
      logs: executionResult.logs,
      error: executionResult.error,
      duration
    };

    // Add description to logs if provided
    if (description) {
      result.logs = [`[Description: ${description}]`, ...result.logs];
    }

    return {
      content: [{
        type: 'text',
        text: JSON.stringify(result)
      }],
      isError: !executionResult.success
    };
  } catch (error) {
    const duration = Date.now() - startTime;

    const result: DoResult = {
      success: false,
      error: error instanceof Error ? error.message : 'Execution failed',
      logs: [],
      duration
    };

    return {
      content: [{
        type: 'text',
        text: JSON.stringify(result)
      }],
      isError: true
    };
  } finally {
    // Cleanup evaluator if it has dispose method
    if ('dispose' in evaluator && typeof evaluator.dispose === 'function') {
      evaluator.dispose();
    }
  }
}
```

