const MAX_DOCUMENT_SIZE = 1_000_000; // 1MB

function serializeDocument(doc: Document): string {
  const json = JSON.stringify(doc, null, 2);

  if (json.length > MAX_DOCUMENT_SIZE) {
    // Return truncated with metadata
    return JSON.stringify({
      _truncated: true,
      _originalSize: json.length,
      _fields: Object.keys(doc),
      ...truncateDocument(doc, MAX_DOCUMENT_SIZE)
    }, null, 2);
  }

  return json;
}
```

### Add Related Documents Hints
```typescript
function findRelatedDocuments(doc: Document): string[] {
  const refs: string[] = [];

  // Find ObjectId references in document
  function scan(obj: unknown, path: string = '') {
    if (typeof obj === 'string' && isObjectId(obj)) {
      refs.push(obj);
    }
    if (typeof obj === 'object' && obj !== null) {
      for (const [key, value] of Object.entries(obj)) {
        scan(value, `${path}.${key}`);
      }
    }
  }

  scan(doc);
  return refs;
}
```

