Implement the Vercel AI SDK adapter.

## Implementation

```typescript
// src/agentfs/adapters/vercel.ts

import { tool } from "ai";
import { z } from "zod";
import type { MonDoAgent } from "../mondo-agent";

export function createMonDoVercelTools(agent: MonDoAgent) {
  return {
    glob: tool({
      description: "Find files matching glob pattern",
      inputSchema: z.object({
        pattern: z.string().describe("Glob pattern like **/*.ts")
      }),
      execute: async ({ pattern }) => agent.glob(pattern)
    }),

    grep: tool({
      description: "Search file contents with regex",
      inputSchema: z.object({
        pattern: z.string().describe("Regex pattern to search"),
        glob: z.string().optional().describe("File filter pattern"),
        caseInsensitive: z.boolean().optional().describe("Case insensitive"),
        maxResults: z.number().optional().describe("Max results")
      }),
      execute: async (args) => agent.grep(args.pattern, args)
    }),

    read: tool({
      description: "Read file contents",
      inputSchema: z.object({
        path: z.string().describe("Absolute file path")
      }),
      execute: async ({ path }) => agent.fs.readFile(path)
    }),

    write: tool({
      description: "Write content to file",
      inputSchema: z.object({
        path: z.string().describe("Absolute file path"),
        content: z.string().describe("Content to write")
      }),
      execute: async ({ path, content }) => {
        await agent.fs.writeFile(path, content);
        return { success: true, path };
      }
    }),

    edit: tool({
      description: "Edit file by replacing text",
      inputSchema: z.object({
        path: z.string(),
        old_string: z.string().describe("Text to find"),
        new_string: z.string().describe("Replacement text")
      }),
      execute: async ({ path, old_string, new_string }) => {
        const content = await agent.fs.readFile(path);
        if (!content) throw new Error("File not found: " + path);
        const updated = content.replace(old_string, new_string);
        await agent.fs.writeFile(path, updated);
        return { success: true, path };
      }
    }),

    list_dir: tool({
      description: "List files in directory",
      inputSchema: z.object({
        path: z.string().describe("Directory path")
      }),
      execute: async ({ path }) => agent.fs.readdir(path)
    })
  };
}
```

